INSERT INTO `magazines` (`id`, `title`, `slug`, `body`, `image`, `mag_category_id`, `user_id`, `view`, `created_at`, `updated_at`) VALUES
(1, 'خط سفید روی نمایشگر گلکسی A71 سامسونگ؛ ماجرا چیست؟\n', 'خط-سفید-روی-نمایشگر-گلکسی-a71-سامسونگ-چیست', ' گیزموچاینا می‌نویسد مشکلی جدید در گوشی‌های هوشمند سامسونگ دیده شده که نمایشگر دستگاه را تحت تأثیر قرار می‌دهد. کاربری در توییتر از نمایش خطی سفید روی نمایشگر گلکسی A71 خبر داده است. پیش‌تر خط سبز، صورتی و ارغوانی روی نمایشگر برخی از گوشی‌های رده‌بالای سامسونگ دیده شده بود.\r\nظاهراً خط سفید به‌صورت عمودی روی نمایشگر گلکسی ای ۷۱ نقش بسته است. کاربری که نخستین‌بار مشکل را گزارش کرد، می‌گوید دستگاهش روی زمین نیفتاده و هیچ‌گونه آسیبی ندیده؛ همچنین شعبه‌ی هند حساب توییتر سامسونگ به این مشکل واکنش نشان داده است.\r\n\r\nمشکل گلکسی A71 در حالی ظاهر می‌شود که گلکسی نوت ۲۰ اولترا نیز پس از دریافت به‌روزرسانی ماه گذشته‌ی اندروید با مشکل خط سبز و صورتی روی نمایشگر مواجه شده بود. سامسونگ در آن زمان اعلام کرد نمایشگر نوت ۲۰ اولترا را به رایگان تعویض می‌کند. ', 'assets/img/blog/post-2.jpg', 3, 1, 1784, '2024-11-01 18:29:42', '2025-05-08 19:02:31'),
(2, 'این‌ بار هوش مصنوعی، تصاویری از کارخانه تولید عروس دریایی را افشا می‌کند!\r\n                                    ', 'این-بار-هوش-مصنوعی-تصاویری-از-کارخانه', '\r\nعروس دریایی‌ها موجوداتی شگفت‌انگیز هستند که بیشتر از 95% بدن آن‌ها از آب تشکیل شده است. آن‌ها مغز یا قلب ندارند و دارای شبکه عصبی ساده‌ای هستند که به آن‌ها کمک می‌کند تا به محیط واکنش نشان دهند. عروس دریایی‌ها با انقباض و انبساط چتر خود در آب حرکت می‌کنند.\r\n\r\nبسیاری از گونه‌های عروس دریایی دارای سلول‌های نیش‌دار هستند که می‌توانند سم را تزریق کنند و برای شکار و دفاع از خود استفاده می‌شوند. آن‌ها از قدیمی‌ترین موجودات زنده در جهان هستند و قدمت آن‌ها به بیش از 500 میلیون سال می‌رسد.\r\n\r\nبعضی از گونه‌های عروس دریایی می‌توانند فرایند پیری را معکوس کنند و دوباره جوان شوند، که به آن‌ها لقب \"عروس دریایی جاودان\" داده شده است.\r\n\r\n', 'assets/img/blog/post-1.jpg', 1, 2, 1217, '2024-08-08 08:00:44', '2025-04-16 08:44:35'),
(3, 'بهترین گوشی های مناسب عکاسی در بازار ایران                                 ', 'بهترین-گوشی-های-مناسب-عکاسی-در-بازار-ایران', 'برای عکاسی در ایران، گوشی‌های مختلفی وجود دارند که می‌توانند به شما کمک کنند. برخی از بهترین گوشی‌های عکاسی که می‌توانید در بازار ایران پیدا کنید عبارتند از\r\n1. Samsung Galaxy S23 Ultra\r\nاین گوشی دارای سیستم دوربین قدرتمندی است که شامل دوربین‌های متعدد با وضوح بالا و قابلیت‌های پیشرفته عکاسی می‌باشد. از جمله ویژگی‌های برجسته آن می‌توان به قابلیت زوم 100 برابری و توانایی ضبط ویدیو با کیفیت 8K اشاره کرد. همچنین، با استفاده از نرم‌افزارهای پیشرفته سامسونگ، می‌توانید عکس‌های خیره‌کننده‌ای بگیرید.\r\n\r\n2. iPhone 14 Pro Max\r\nآیفون همیشه به عنوان یکی از بهترین گوشی‌ها برای عکاسی شناخته شده است. آیفون 14 پرو مکس نیز از این قاعده مستثنی نیست. این گوشی دارای سیستم دوربین سه‌گانه با قابلیت‌های مانند حالت شب، HDR هوشمند و دیپ فیوژن است. این ویژگی‌ها به شما کمک می‌کند تا عکس‌های فوق‌العاده با کیفیت در هر شرایط نوری بگیرید.\r\n\r\n3. Sony Xperia 1 IV\r\nاین گوشی سونی با تمرکز بر عکاسی و فیلم‌برداری حرفه‌ای طراحی شده است. با لنز‌های معتبر Zeiss و حسگرهای بزرگ، Xperia 1 IV به شما امکان می‌دهد عکس‌هایی با کیفیت بالا و جزئیات دقیق بگیرید. همچنین، این گوشی از قابلیت ضبط ویدیو با کیفیت 4K HDR و فریم‌ریت بالا برخوردار است.\r\n\r\n4. Google Pixel 7 Pro\r\nسری گوشی‌های گوگل پیکسل به خاطر دوربین‌های قدرتمند و نرم‌افزارهای عکاسی پیشرفته خود شناخته شده‌اند. Google Pixel 7 Pro با استفاده از فناوری HDR+ و الگوریتم‌های پیشرفته‌ی پردازش تصویر، عکس‌های فوق‌العاده‌ای را حتی در شرایط نوری ضعیف ارائه می‌دهد. این گوشی همچنین دارای ویژگی‌های جدیدی مانند Magic Eraser برای حذف اشیاء ناخواسته از عکس‌ها است.\r\n\r\n5. OnePlus 11\r\nاین گوشی نیز با دوربین‌های متعدد و تکنیک‌های پیشرفته‌ی عکاسی، یک گزینه مناسب برای علاقه‌مندان به عکاسی است. OnePlus 11 دارای دوربین‌های متنوع با قابلیت‌هایی مانند زوم اپتیکال و حالت شب پیشرفته می‌باشد. همچنین، رابط کاربری آسان و امکانات متعدد آن، تجربه عکاسی شما را بهبود می‌بخشد.\r\n\r\nجمع‌بندی\r\nهر یک از این گوشی‌ها دارای ویژگی‌های منحصر به فردی است که برای عکاسی مناسب است. با توجه به نیازها و بودجه خود، می‌توانید یکی از این گوشی‌ها را انتخاب کنید و از تجربه‌ی عکاسی با کیفیت بالا لذت ببرید.', 'assets/img/blog/post-3.jpg', 2, 3, 1673, '2024-11-07 18:36:35', '2025-05-08 19:02:43'),
(4, '                                        هزینه ساخت بازی‌های انحصاری پلی استیشن با فیلم‌های بلاک‌باستر برابری می‌کند\n                                    ', 'هزینه-ساخت-بازی-های-انحصاری-پلی-استیشن', 'ساخت بازی‌های انحصاری پلی استیشن هزینه‌های بسیار بالایی دارد و می‌تواند با هزینه ساخت فیلم‌های بلاک‌باستر مقایسه شود. این هزینه‌ها شامل موارد زیر می‌شوند:\r\n\r\n1. **توسعه بازی**: توسعه‌دهندگان بازی‌های انحصاری پلی استیشن از تیم‌های بزرگ و حرفه‌ای تشکیل شده‌اند که برای طراحی، برنامه‌نویسی و تست بازی‌ها به زمان و هزینه‌های زیادی نیاز دارند.\r\n\r\n2. **گرافیک و صدا**: بازی‌های انحصاری پلی استیشن اغلب از بهترین کیفیت‌های گرافیکی و صدای بالا برخوردارند. طراحی محیط‌های بازی، شخصیت‌ها و جلوه‌های ویژه نیازمند هزینه‌های بسیاری است. همچنین، ضبط صدا و موسیقی نیاز به استفاده از استودیوهای حرفه‌ای دارد.\r\n\r\n3. **بازاریابی و تبلیغات**: مانند فیلم‌های بلاک‌باستر، بازی‌های بزرگ نیز به بازاریابی و تبلیغات گسترده‌ای نیاز دارند تا مخاطبان بیشتری جذب کنند. این شامل تبلیغات در تلویزیون، اینترنت و رسانه‌های اجتماعی است.\r\n\r\n4. **روند تولید**: روند تولید بازی‌های بزرگ ممکن است چندین سال طول بکشد و هزینه‌های زیادی برای نگهداری تیم‌های توسعه و منابع لازم نیاز دارد.\r\n\r\nبازی‌هایی مانند **Horizon Forbidden West** و **The Last of Us Part II** که از بازی‌های محبوب پلی استیشن هستند، هزینه‌های تولید بسیار بالایی دارند. برای مثال، هزینه ساخت و توسعه این بازی‌ها به بیش از ۲۰۰ میلیون دلار می‌رسد، که این رقم معادل با هزینه ساخت فیلم‌های بلاک‌باستر است.\r\n\r\nبنابراین، ساخت بازی‌های انحصاری پلی استیشن نه تنها از نظر کیفیت و تجربه بازی برتر است، بلکه از نظر هزینه‌ها نیز با فیلم‌های بزرگ هالیوودی رقابت می‌کند.', 'assets/img/blog/post-4.jpg', 4, 6, 1011, '2024-09-09 09:06:35', '2025-05-08 19:03:59'),
(5, '                                         یک دهه سکوت شکست؛ مایکروسافت آمار فروش ایکس باکس را فاش کرد!', 'یک-دهه-سکوت-شکست-مایکروسافت-آمار-فروش-ایکس-باکس', 'مایکروسافت اخیراً آمار فروش گوشی‌های Xbox خود را فاش کرد. در سال ۲۰۲۳، مجموع فروش گوشی‌های Xbox Series X و Series S به ۷.۵ میلیون دستگاه رسید، که نسبت به سال قبل کاهش شده است. این میزان فروش نشان‌دهنده یکی از دلایل شکست ده ساله مایکروسافت در این زمینه است.\r\nآمار و اطلاعات کلیدی:\r\nفروش کنسول‌ها:\r\n\r\nمجموع فروش کنسول‌های Xbox Series X و Series S به بیش از ۲۰ میلیون دستگاه رسید.\r\n\r\nاین آمار نشان‌دهنده رشد قابل توجهی نسبت به نسل قبلی کنسول‌های Xbox است.\r\n\r\nدلایل موفقیت:\r\n\r\nقدرت و عملکرد: Xbox Series X به عنوان قوی‌ترین کنسول بازی بازار شناخته می‌شود.\r\n\r\nدسترسی به بازی‌ها: با اشتراک Game Pass، کاربران می‌توانند به صدها بازی دسترسی داشته باشند. این خدمت اشتراکی توانسته مخاطبان زیادی را به سمت Xbox جذب کند.\r\n\r\nاستراتژی‌های بازاریابی:\r\n\r\nهمکاری با توسعه‌دهندگان: مایکروسافت با استودیوهای بازی‌سازی مختلف همکاری کرده است تا بازی‌های انحصاری و جذاب برای کنسول خود ارائه دهد.\r\n\r\nتمرکز بر تجربه کاربر: بهبود رابط کاربری و اضافه کردن ویژگی‌های نوآورانه به دستگاه‌ها، تجربه کاربری را ارتقاء داده است.\r\n\r\nچالش‌ها و رقابت:\r\n\r\nرقابت با پلی استیشن: با وجود موفقیت‌های Xbox، همچنان پلی استیشن یکی از رقبای اصلی در بازار کنسول‌های بازی است. اما مایکروسافت با استراتژی‌های نوآورانه خود توانسته جایگاه محکمی در بازار داشته باشد.\r\n\r\nجمع‌بندی:\r\nانتشار آمار فروش Xbox توسط مایکروسافت نشان می‌دهد که این شرکت با استراتژی‌های هوشمندانه و تمرکز بر نیازهای کاربران، توانسته است در رقابت با سایر کنسول‌های بازی موفق عمل کند. Xbox Series X و Series S با امکانات و ویژگی‌های برجسته خود، جایگاه ویژه‌ای در میان علاقه‌مندان به بازی‌های ویدیویی پیدا کرده‌اند.\r\n', 'assets/img/blog/post-5.jpg', 6, 5, 214, '2024-01-18 16:11:11', '2025-01-26 13:51:24'),
(6, 'بررسی تلویزیون 4K اسنوا؛ ۵۵ اینچی اقتصادی برای فیلم‌بازها!', 'بررسی-تلویزیون-k-اینچی-اقتصادی-برای-فیلم-باز-ها', 'جایگزین کن خودت\r\nمطمئناً! تلویزیون‌های 55 اینچی اقتصادی می‌توانند گزینه‌ی بسیار مناسبی برای فیلم‌بازها باشند، به خصوص اگر به دنبال کیفیت تصویر 4K با قیمت مناسب باشید. در اینجا بررسی چند تلویزیون 55 اینچی اقتصادی آورده شده است:\r\n\r\n1. تلویزیون 4K سامسونگ مدل TU7000\r\nاین تلویزیون با صفحه نمایش 4K و تکنولوژی کریستال، کیفیت تصویر بسیار خوبی را ارائه می‌دهد. همچنین دارای سیستم عامل Tizen است که به شما امکان دسترسی به اپلیکیشن‌های مختلف مانند نتفلیکس و یوتیوب را می‌دهد.\r\n\r\n2. تلویزیون 4K ال‌جی مدل UN7300\r\nاین مدل از ال‌جی با رزولوشن 4K و پشتیبانی از HDR10، تجربه تماشای فوق‌العاده‌ای را فراهم می‌کند. سیستم عامل WebOS و کنترل جادویی ال‌جی، کار با این تلویزیون را راحت و لذت‌بخش کرده است.\r\n\r\n3. تلویزیون 4K شیائومی مدل Mi TV 4S\r\nتلویزیون شیائومی با کیفیت تصویر 4K و پشتیبانی از HDR، یکی از بهترین گزینه‌های اقتصادی است. این تلویزیون دارای سیستم عامل اندروید تی‌وی است که به شما امکان دسترسی به هزاران اپلیکیشن را می‌دهد.\r\n\r\n4. تلویزیون 4K هایسنس مدل H55B7100\r\nاین تلویزیون با کیفیت تصویر 4K و پشتیبانی از HDR10، گزینه‌ای بسیار مناسب برای افرادی است که به دنبال تلویزیونی با کیفیت و قیمت مناسب هستند. سیستم عامل Vidaa U نیز امکان دسترسی به اپلیکیشن‌های مختلف را فراهم می‌کند.\r\n\r\nجمع‌بندی\r\nاین تلویزیون‌های 55 اینچی اقتصادی می‌توانند گزینه‌های بسیار خوبی برای تماشای فیلم باشند. هر کدام از این تلویزیون‌ها دارای ویژگی‌های منحصر به فردی هستند که تجربه‌ی تماشای شما را بهبود می‌بخشند. با توجه به نیازها و بودجه‌ی خود، می‌توانید یکی از این مدل‌ها را انتخاب کنید و از تماشای فیلم‌های مورد علاقه‌تان لذت ببرید.', 'assets/img/blog/post-6.jpg', 5, 4, 350, '2023-12-19 09:59:00', '2025-01-26 13:40:09'),
(7, 'اپل احتمالاً روی نمایشگر 90 هرتزی برای آیپد ایر، آی‌مک و استودیو دیسپلی کار می‌کند', 'اپل-احتمالاً-روی-نمایشگر-برای-آیپد-ایر-آی-مک', 'شایعه شده است اپل نمایشگری را با نرخ تازه‌سازی 90 هرتزی توسعه می‌دهد که می‌تواند در آیپد ایر، آی‌مک 24 اینچی و استودیو دیسپلی استفاده شود.\r\n\r\nبه گزارش 9to5Mac، ظاهراً اپل مشغول ساخت صفحه‌نمایشی با نرخ رفرش 90 هرتز است. این شایعه از منبع ناشناسی به‌ دست پادکست Upgrade رسیده است.\r\n\r\nآیپد ایر، آی‌مک 24 اینچی و استودیو دیسپلی درحال‌حاضر همگی نمایشگرهایی با نرخ تازه‌سازی 60 هرتز دارند. این شایعه حاکی از آن است که پنل 90 هرتزی ابتدا در آیپد ایر M3 ظاهر می‌شود و درنهایت راه خود را به آی‌مک 24 اینچی و استودیو دیسپلی باز می‌کند.', 'assets/img/blog/post-7.webp', 2, 5, 473, '2024-11-01 21:33:46', '2025-05-08 19:02:43'),
(8, 'ساخت دنیای مجازی فوق‌طبیعی با هوش مصنوعی مولد و دوربین‌های پیشرفته ممکن می‌شود', 'ساخت-دنیای-مجازی-فوق‌طبیعی-با-هوش-مصنوعی-مولد-و-دوربین‌های-پیشرفته-ممکن-می‌شود', 'هوش مصنوعی مولد می‌تواند تصاویر، متن‌ها و حتی آهنگ‌های متنوعی تولید کند اما چه می‌شود اگر از آن برای ساخت دنیاهای مجازی با شباهت بسیار زیاد به دنیای واقعی استفاده کنیم؟ در همین راستا، استارتاپ تازه‌تأسیس Odyssey قصد دارد کاربرد مدل‌های مولد را یک پله ارتقا دهد و امکان ساخت دنیاهای سینماتیک را در اختیار توسعه‌دهندگان بگذارد. البته برای دستیابی به چنین هدفی نه‌فقط به مدل نرم‌افزاری پیشرفته نیاز است، بلکه از دوربین‌های خاصی هم برای جمع‌آوری داده استفاده می‌شود.\r\n\r\nاستارتاپ ادیسه اعلام کرده دوربین‌های خاصی با مکانیسم ثبت تصویر پیشرفته را توسعه داده که از آنها به‌منظور جمع‌آوری داده از محیط‌های مختلف بهره خواهد گرفت. هرکدام از این دوربین‌ها حدود ۱۱ کیلوگرم وزن دارند و در قالب کوله‌پشتی روی دوش افراد قرار می‌گیرند. ازجمله امکانات آنها می‌توان به ۶ لنز مختلف و ۲ حسگر لایدار اشاره کرد که در کنار یک دستگاه سنجش لَختی (IMU) با ترکیبی از شتاب‌سنج‌ها و ژیروسکوپ‌ها قرار می‌گیرند.', 'assets/img/blog/post-8.gif', 1, 1, 118, '2024-11-16 11:49:35', '2025-04-16 08:44:35'),
(9, 'OpenAI با جذب مدیر باتجربه متا، هوش مصنوعی را به دنیای فیزیکی می‌آورد', 'openai-با-جذب-مدیر-باتجربه-متاهوش-مصنوعی-', '«کیتلین کالینوفسکی» (Caitlin Kalinowski)، مدیر سابق بخش عینک‌های AR، متا اعلام کرد به شرکت OpenAI پیوسته است. کالینوفسکی در پروژه عینک هوشمند Orion متا نیز نقش داشته است؛ حالا او مدیر بخش رباتیک و سخت‌افزار شرکت OpenAI خواهد بود و می‌خواهد هوش مصنوعی را به دنیای فیزیکی بیاورد.\r\n\r\nکیتلین کالینوفسکی با انتشار پستی در لینکدین گفت به OpenAI پیوسته تا بخش سخت‌افزار این شرکت را رهبری کند. او درباره این شرکت هوش مصنوعی می‌گوید:«OpenAI و ChatGPT درحال‌حاضر دنیا را تغییر داده‌ و نحوه دریافت و تعامل مردم با اطلاعات را بهبود بخشیده‌اند و مزایای معناداری در سراسر جهان ارائه می‌دهند. اکنون هوش مصنوعی هیجان‌انگیزترین جبهه مهندسی در بخش فناوری محسوب می‌شود و من از اینکه بخشی از تیم OpenAI هستم، بسیار هیجان‌زده‌ام.»', 'assets/img/blog/post-9.webp', 1, 2, 107, '2024-11-18 19:35:58', '2025-04-16 08:44:35'),
(10, 'آموزش حذف اپل آیدی که پسورد آن را فراموش کرده‌ایم', 'آموزش-حذف-اپل-آیدی-که-پسورد', 'خارج‌شدن از اپل ایدی بدون رمز عبور از اپ استور یا iTunes\r\nاگر می‌خواهید Apple ID که در iCloud و اپ استور از آن استفاده می‌کنید را بدون رمز عبور حذف کنید، باید مراحل زیر را دنبال کنید:\r\n\r\nبه تنظیمات بروید و روی پروفایل خود در بالای صفحه ضربه بزنید.\r\nسپس گزینه iTunes & App Store را انتخاب کنید.\r\nدر آخرین مرحله یک‌بار روی Apple ID خود و سپس روی Sign Out باید ضربه بزنید.با انجام این مراحل، اپل ایدی شما از اپ استور حذف می‌شود.\r\n\r\n', 'assets/img/blog/post-10.webp', 4, 3, 257, '2024-07-16 22:24:54', '2025-05-08 19:03:59'),
(11, 'مایکروسافت ظاهراً تنظیمات ویندوز 11 برای برنامه‌های پیش‌فرض را بازطراحی می‌کند', 'مایکروسافت-ظاهراً-تنظیمات-ویندوز-11-برای-برنامه-های-پیش-فرض', 'این ماجرا از زمانی آغاز شد که ویندوز ۱۱ برای اولین بار عرضه شد و ارائه‌دهندگان برنامه‌های شخص ثالث، مایکروسافت را به انجام اقدامات ناعادلانه متهم کردند. این اقدامات تأثیر منفی بر‌‌ روی توسعه‌دهندگان ویندوزی داشت. برای مثال مرورگر ویوالدی «Vivaldi»، مایکروسافت را به دلیل رفتارهای انحصارطلبانه و ناامید‌کننده متهم کرد، و خواستار دخالت اتحادیه اروپا نیز شد. همچنین اوایل امسال، مایکروسافت مخفیانه درایوری به‌نام «UCPD» را اضافه کرد تا تلاش‌های برخی کاربران برای اعمال تغییرات در رجیستری و تغییر برنامه‌های پیش‌فرض را مسدود کند.\r\n\r\nبه‌گزارش نئووین، حالا به‌‌نظر می‌رسد این شرکت به‌دنبال ایجاد برخی تغییرات در رابط کاربری بخش تنظیمات ویندوز ۱۱ برای انتخاب برنامه‌های پیش‌فرض است. همانطور که کاربری در شبکه اجتماعی ایکس به‌نام «Albacore» کشف کرده، در‌حال‌حاضر این صفحه که در دست تغییر قرار دارد، پر از جانگهدارها و توضیحات نامرتب است.', 'assets/img/blog/post-11.webp', 5, 4, 105, '2024-10-10 08:41:35', '2025-01-26 13:41:17'),
(12, 'سونی و شیائومی دستبند سلامتی ویژه‌ای با طرح پلی‌استیشن معرفی کردند\r\n', 'سونی-و-شیائومی-دستبند-سلامتی-با-طرح-پلی-استیشن-معرفی-کردند\r\n', 'به‌مناسبت دهمین سالگرد فعالیت کسب‌وکار پلی‌استیشن در چین، سونی در همکاری غیرمنتظره‌ای با شیائومی دستبند ویژه پلی‌استیشن معرفی کرده است. این دستبند ظاهراً نسخه‌ای ویژه از شیائومی اسمارت بند 9 پرو است و طراحی منحصربه‌فردی با برند PlayStation دارد.\r\nطبق گزارش «Gizmochina»، طراحی این دستبند ویژه تغییرات قابل‌توجهی ندارد اما ویژگی اصلی آن طراحی مخصوص پلی‌استیشن است که شامل آیکون‌های دکمه‌های پلی‌استیشن و لوگوی مشهور آن می‌شود. بسته‌بندی این دستبند شامل یک بند مشکی معمولی، کارت یادبود و کابل شارژ استاندارد است.', 'assets/img/blog/post-12.webp', 7, 2, 116, '2025-01-22 17:16:19', '2025-04-16 08:45:58');

